#!/bin/sh
# This is used on an exploded GDB tarball when doing merges (removes the directories we don't ship).
rm -rf contrib cpu gdb/gdbtk/plugins sim/arm sim/cris sim/d10v sim/erc32 sim/frv sim/h8300 sim/igen sim/iq2000 sim/m32c sim/m32r sim/m68hc11 sim/mcore sim/mips sim/mn10300 sim/ppc sim/sh sim/v850 sim/testsuite/common sim/testsuite/config sim/testsuite/d10v-elf sim/testsuite/frv-elf sim/testsuite/lib sim/testsuite/m32r-elf sim/testsuite/mips64el-elf sim/testsuite/sim md5.sum
# Remove generated files, Insight doesn't carry these in their repository either.
rm -f bfd/po/*.gmo opcodes/po/*.gmo bfd/doc/*.info etc/*.info gdb/doc/*.info gdb/doc/*.info-? bfd/doc/aoutx.texi  bfd/doc/archive.texi bfd/doc/archures.texi bfd/doc/bfdt.texi bfd/doc/cache.texi bfd/doc/coffcode.texi bfd/doc/core.texi bfd/doc/elf.texi bfd/doc/elfcode.texi  bfd/doc/format.texi bfd/doc/libbfd.texi bfd/doc/bfdwin.texi bfd/doc/bfdio.texi bfd/doc/opncls.texi  bfd/doc/reloc.texi bfd/doc/section.texi bfd/doc/syms.texi bfd/doc/targets.texi bfd/doc/init.texi bfd/doc/hash.texi bfd/doc/linker.texi bfd/doc/mmo.texi gdb/c-exp.c gdb/cp-name-parser.c gdb/objc-exp.c gdb/ada-lex.c gdb/ada-exp.c gdb/jv-exp.c gdb/f-exp.c gdb/m2-exp.c gdb/p-exp.c
